(() => {
    const foo = {
        name: 'foo',
        amount: 4000,
        deduct: function (amount) {
            this.amount -= amount
            return `Осталось ${this.amount}`
        }
    }

    const bar = {
        name: 'bar',
        amount: 6000
    }

    const amountBy = total =>
        foo.deduct.bind(bar, total)

    console.log(amountBy(1000)())
    console.log(amountBy(2000)())
})()

(() => {
    const foo = {
        name: 'foo',
        amount: 4000,
        deduct: function (amount) {
            this.amount -= amount
            return `Осталось ${this.amount}`
        }
    }

    const bar = {
        name: 'bar',
        amount: 6000
    }

    const amountBy = total =>
        foo.deduct.apply(bar, [total])

    console.log(amountBy(1000))
    console.log(amountBy(2000))
})()

