const calc = a => b => c => d => {
    switch (d) {
        case '*': return a * b * c
        case '/': return a / b / c
        case '+': return a + b + c
        case '-': return a - b - c
        default: return 'Некорректная операция!'
    }
}

console.log(calc(1)(2)(3)('*'))
console.log(calc(2)(3)(4)('+'))
console.log(calc(6)(3)(1)('-'))
console.log(calc(6)(3)(2)('x'))

